import React, { useState } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import styles from "./CheckoutForm.module.css";
import AddressForm from "./components/AddressForm/AddressForm";
import Products from "../Products/Products";

const productsData = [
  {
    name: "Yongqiang Accent Chair",
    price: 50,
    imageUrl: "https://m.media-amazon.com/images/I/71rFOAGdoOS._AC_SX466_.jpg",
  },
  {
    name: "Modern Accent Chair",
    price: 50,
    imageUrl:
      "https://target.scene7.com/is/image/Target/AccentChairs_QUIVER-210420-1618938887124",
  },
];

const CheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();
  const [showPaymentMethod, setShowpaymentMethod] = useState(false);
  const [address, setAddress] = useState({});
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!stripe || !elements) {
      return;
    }

    const cardElement = elements.getElement(CardElement);
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: cardElement,
    });

    if (error) {
      console.log("[error]", error);
    } else {
      console.log("[PaymentMethod]", paymentMethod);
    }
  };

  const handleAddressSubmit = (data) => {
    console.log(data);
    setShowpaymentMethod(true);
    setAddress(data);
  };

  return (
    <div className={styles.container}>
      <Products data={productsData} />
      <AddressForm handleAddressSubmit={handleAddressSubmit} />
      <h4>Step2: Payment</h4>
      <form
        onSubmit={handleSubmit}
        className={`${styles.form} ${!showPaymentMethod && styles.disable}`}
      >
        <div className={styles.inputContainer}>
          <CardElement />
        </div>
        {showPaymentMethod && (
          <button className={styles.btn} type="submit" disabled={!stripe}>
            Submit
          </button>
        )}
      </form>
    </div>
  );
};

export default CheckoutForm;
