import React, { useEffect } from "react";
import TextField from "@mui/material/TextField";
import { Country, State, City } from "country-state-city";
import styles from "./AddressForm.module.css";
const AddressForm = ({ cb = () => {} }) => {
  const [values, setValues] = React.useState({
    fullName: "",
    phoneNumber: "",
    selectedState: "",
    city: "",
  });

  //   useEffect(() => {
  //     setValues({
  //       ...values,
  //       city: "New York",
  //     });
  //   }, [values.selectedState]);

  const $states = State.getStatesOfCountry("US");
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  };
  const handleSelectChange = (e) => {
    let index = e.target.selectedIndex;
    console.log($states[index]);
    setValues({
      ...values,
      selectedState: e.target.value,
      city: City.getCitiesOfState("US", $states[index]?.isCode),
    });
  };

  const handleSubmit = () => {
    console.log(values);
  };
  return (
    <form onSubmit={handleSubmit} className={styles.form}>
      <h4>Step1: Address</h4>
      <div className={styles.inputContainer}>
        <TextField
          className={styles.w100}
          label="Full Name"
          onChange={handleChange}
          name="fullName"
          value={values.fullName}
          variant="outlined"
        />
      </div>
      <div className={styles.inputContainer}>
        <TextField
          className={styles.w100}
          label="Phone Number"
          onChange={handleChange}
          name="phoneNumber"
          value={values.phoneNumber}
          variant="outlined"
        />
      </div>
      <div className={styles.inputContainer}>
        <select
          style={{ pointerEvents: "none" }}
          disabled
          className={styles.select}
          value="United States"
        >
          <option value="United States">United States</option>
        </select>
      </div>

      <div className={styles.inputContainer}>
        <select
          className={styles.select}
          value={values.selectedState}
          onChange={handleSelectChange}
        >
          {$states?.map((state, index) => (
            <>
              {index === 0 && <option selected>Select State</option>}
              <option id={state.isoCode} value={state.name}>
                {state.name}
              </option>
            </>
          ))}
        </select>
      </div>
      <div className={styles.inputContainer}>
        {/* <select
          className={styles.select}
          value={values.selectedState}
          onChange={handleSelectChange}
        >
          {$country?.map((country, index) => (
            <>
              {index === 0 && <option selected>Select country</option>}
              <option value={country.name}>{country.name}</option>
            </>
          ))}
        </select> */}
      </div>
    </form>
  );
};

export default AddressForm;
